$(document).ready(function(){
 
    //Menu from the left
    var slideLeft = new Menu({
        wrapper: '.o-wrapper',
        type: 'slide-left',
        menuOpenerClass: '.c-button',
        maskId: '#c-mask',
        closeBtn: '.c-button'
    });

    var slideLeftBtn = document.querySelector('.left-menu-opener');

    if (slideLeftBtn != null ){
        slideLeftBtn.addEventListener('click', function(e) {
            e.preventDefault;
            slideLeft.open();
        }); 
    }

    //Menu from the right
    var slideRight = new Menu({
        wrapper: '.o-wrapper',
        type: 'slide-right',
        menuOpenerClass: '.c-button',
        maskId: '#c-mask',
        closeBtn: '.c-button'
      });
      
      var slideRightBtn = document.querySelector('.right-menu-opener');
      
      if (slideRightBtn != null ){
        slideRightBtn.addEventListener('click', function(e) {
            e.preventDefault;
            slideRight.open();
        });
    }

    //password view

    function viewValue (element, button){
        button.on('click', function(){
            if (element.prop('type') != 'undefined'){

                if (element.prop('type') == 'text'){
                   element.prop('type', 'password');
                } else {
                    element.prop('type', 'text' ).prop('autocomplete', 'off');

                }
            }
        });
    }

   viewValue($('#password'), $('.fa-eye'));
   viewValue($('#registration_password'), $('.fa-eye'));
   viewValue($('#ChangePasswordType_password'), $('.fa-eye.pass'));
   viewValue($('#ChangePasswordType_newpassword_first'), $('.fa-eye.new-pass'));
   viewValue($('#ChangePasswordType_newpassword_second'), $('.fa-eye.new-pass-sec'));

   // Hide Header on on scroll down
    var didScroll,
        lastScrollTop = 0,
        delta = 5,
        navbarHeight = $('.hidding-nav .navbar-fixed-top').outerHeight();

    $(window).scroll(function(event){
        didScroll = true;
    });

    setInterval(function() {
        if (didScroll) {
            hasScrolled();
            didScroll = false;
        }
    }, 250);

    function hasScrolled() {
        var st = $(this).scrollTop();
        
        // Make sure they scroll more than delta
        if(Math.abs(lastScrollTop - st) <= delta)
            return;
        
        // If they scrolled down and are past the navbar, add class .nav-up.
        // This is necessary so you never see what is "behind" the navbar.
        if (st > lastScrollTop && st > navbarHeight){
            // Scroll Down
            $('.hidding-nav .navbar-fixed-top').removeClass('nav-down').addClass('nav-up');
        } else {
            // Scroll Up
            if(st + $(window).height() < $(document).height()) {
                $('.hidding-nav .navbar-fixed-top').removeClass('nav-up').addClass('nav-down');
            }
        }
        
        lastScrollTop = st;
    }

    //set wh to sections, avoiding 100vh by css that causes a glicth in the bg and scroll
    var bg = $("#section1, #section4, #section7");
   
    function resizeBackground() {
        bg.height($(window).height() );
    }
    resizeBackground();

    ///IOS
    $( window ).on( "orientationchange", function( ) {
         resizeBackground();
     });


    //snippet to detect ios and android
    var classNames = [],
        html = document.getElementsByTagName('html')[0];

    if (navigator.userAgent.match(/(iPad|iPhone|iPod)/i)) {
        classNames.push('device-ios');  
    } 
    if (navigator.userAgent.match(/android/i)) {
        classNames.push('device-android');   
    } 

    if (classNames.length) {
        classNames.push('on-device');
    }

    if (html.classList){
        html.classList.add.apply(html.classList, classNames);
    } 

    //avoid jumping on menu links 
    $('.navmenu-fixed-left .panel-title, .panel-blue .panel-title').click(function(event) {
        event.preventDefault(); 
    });
});

