function addEventSubscribers(vodPlayer) {
    var validateInterval = null;

    vodPlayer.on('userinactive', function() {
        if (!this.paused()) {
            $('.video-js-title').removeClass('show').addClass('hide');
        }
    });

    vodPlayer.on('ended', function() {
        $('.video-js-title').removeClass('hide').addClass('show');
        this.loadingSpinner.show();
        this.load();
        window.document.location.href = back_path;
    });

    vodPlayer.on('useractive', function() {
        if (!this.paused()) {
            $('.video-js-title').removeClass('hide').addClass('show');
        }
    });

    vodPlayer.on('timerEvent', function() {
        eventData.eventName = 'CurrentTime';
        eventData.CurrentTime = this.currentTime();
        eventData.Duration = this.duration();
        $.post(path, JSON.stringify(eventData)).then(function(data) {});
    });

    validateInterval = setInterval(function() {
        validateGeoIpPath = Routing.generate('service_validate_geoip', {});
        console.log('ruta: ' + validateGeoIpPath);
        $.post(validateGeoIpPath).then(function(response) {
            if (false === response.Status) {
                window.location.href = back_path;
            }
        });
    }, 600000);

    $(document).on('click', '#vid1_html5_api, .vjs-play-control', function() {
        if (vodPlayer.paused()) {
            $('.video-js-title').removeClass('hide').addClass('show');
        }
    });

    var playerType = document.getElementById('player-type').value;

    if ('shaka' === playerType) {
        window.VodYB.initYoubora(vodPlayer.tech({IWillNotUseThisInPlugins: true}).shakaPlayer);
    } else if ('hls' === playerType) {
        window.VodYB.initYoubora(vodPlayer.tech({IWillNotUseThisInPlugins: true}).hls_);
    } else if ('videojs' === playerType) {
        //window.VodYB.initYoubora(vodPlayer);
    }
}